	var curpage = 0;
	var perpage = 10;
	index.onlineUsersDetail = function(){
		if(typeof chat == 'undefined'){
			loader.getChatFile();
		}
		var onlineUsersData1 = $.data(Array, 'onlineUsersData');
		if(typeof onlineUsersData1 != 'undefined' && onlineUsersData1 != null && onlineUsersData1.length > 0){
			var height = index.getClientHeight();
			var lrb = height - 50;
			var inner = '<div class="wrap"><header><div class="return" onclick="index.init();"></div><div class="header-text">寻找有缘人</div></header>';
			inner += '<section id="listOnlineUsersScroll" style="height:'+lrb+'px;"><section class="list_spe_style"><ul class="user_sub_list user_sub_list1">';
			var	width = document.documentElement.clientWidth - 90;
			for(var a=curpage*perpage; a<((curpage+1)*perpage>=onlineUsersData1.length?onlineUsersData1.length:(curpage+1)*perpage); a++){
				inner += '<li>';
				inner += '<div  onclick="index.toChatFromOnline(\''+onlineUsersData1[a].idUser+'\', \''+onlineUsersData1[a].nickname+'\',\'main\');"style="float:left;width:'+width+'px;height:60px">';
				inner += '<div class="person_img"><img alt="" src="../images/html5/1.jpg"><em class="icon icon-news2"></em></div>';
				inner += '<div class="user_cont">';
				inner += '<p class="name">'+index.subString(onlineUsersData1[a].nickname, 16)+'</p>';
				inner += '<p class="mood">'+index.subString(onlineUsersData1[a].impresa, 16)+'</p>';
				inner += '</div><div id="toChatId'+onlineUsersData1[a].idUser+'" style="float:left;width:20px;height:20px;"></div>';
				inner += '</div>';
				inner += '<div class="select" onclick="index.addFriendSubmit(\''+ onlineUsersData1[a].idFetion +'\', \'1\', \'\', this)"><div class="hx"></div><div class="sx"></div></div></li>';
			}
			if(onlineUsersData1.length > perpage){
				inner += '<a href="javascript:index.onlineUsersDetail()"><li><div class="person_more">换一批</div></li></a>';
			}
			inner += '</ul></section></section></div>';
			$('body').html(inner);			
			curpage++;
			var pagenum = 0;
			if((onlineUsersData1.length % perpage) == 0){
				pagenum = onlineUsersData1.length / perpage;
			}else{
				pagenum = parseInt(onlineUsersData1.length / perpage) + 1;
			}
			if(curpage >= pagenum){
				curpage = 0;
			}
			scroll = new iScroll('listOnlineUsersScroll', {hScroll:false, hideScrollbar:true, bounce:false});
			index.hideLocationBar();
		}else{
			index.showToRemind('没有寻找到你的有缘人');
		}
	}
	
