	//本地保存最近联系人
	index.saveLocalRecentContact = function(msg){
		if(!loader.localstorage()){
			return;
		}
		//本地缓存获取最近联系人
		var recentContact = eval('('+localStorage.getItem('recentContact')+')');
		var contacts = 0;
		var hasin = false;
		if(typeof recentContact!='undefined' && recentContact!=null){
			for(var contact in recentContact){
				contacts++;
				//判断是否为当前用户的最近联系人
				if(contact == idUser){
					hasin = true;
					//获取最近联系人列表
					var recentMsgs = recentContact[contact];
					if(typeof recentMsgs!='undefined' && recentMsgs!=null && recentMsgs.length>0){
                        //增加最近联系人
						if(typeof msg.fromIdUser != 'undefined'){
							for(var j=0; j<recentMsgs.length; j++){
								if(msg.fromIdUser == recentMsgs[j].fromIdUser){
									recentMsgs = index.removeIndexOf(j, recentMsgs);
									break;
								}
							}
						}else if(typeof msg.groupUri != 'undefined'){
							for(var j=0; j<recentMsgs.length; j++){
								if(msg.groupUri == recentMsgs[j].groupUri){
									recentMsgs = index.removeIndexOf(j, recentMsgs);
									break;
								}
							}
						}
						recentContact[contact] = index.addRecentContact(recentMsgs, msg);
					}
				}
			}
		}else{
			recentContact = {};
		}
		//缓存未满3个人的最近联系人，且当前用户未缓存
		if(contacts<3 && !hasin){
            //增加最近联系人
			recentContact[idUser] = {};
			recentContact[idUser] = new Array();
			recentContact[idUser].unshift(msg);
		}
		//如果最近联系人超过10个，截取最近联系人
		if(typeof recentContact[idUser]!='undefined' && recentContact[idUser]!=null && recentContact[idUser].length>0){
			var recentMsgs = recentContact[idUser];
			if(recentMsgs.length > 10){
				recentMsgs = recentMsgs.slice(0, 10);
			}
			recentContact[idUser] = recentMsgs;
			
			//本地保存最近联系人
			localStorage.setItem('recentContact', JSON.stringify(recentContact));
		}
	}
	
	//比较最近联系人时间，按时间排序
	index.addRecentContact = function(recentMsgs, msg){
		if(recentMsgs && recentMsgs.length > 0){
			for(var i=0; i<recentMsgs.length; i++){
				var stimeRecent = null;
				if(typeof recentMsgs[i].sendTime != 'undefined'){
					stimeRecent = recentMsgs[i].sendTime;
				}else if(typeof recentMsgs[i].sendtime != 'undefined'){
					stimeRecent = recentMsgs[i].sendtime;
				};
				var stimeMsg = null;
				if(typeof msg.sendTime != 'undefined'){
					stimeMsg = msg.sendTime;
				}else if(typeof msg.sendtime != 'undefined'){
					stimeMsg = msg.sendtime;
				};
				if(stimeRecent.replace(/[\s-:]/gi, '') < stimeMsg.replace(/[\s-:]/gi, '')){
					recentMsgs.splice(i, 0, msg);
					break;
				}else if(recentMsgs.length -1 == i){
					recentMsgs.push(msg);
					break;
				}
			}
		}else if(recentMsgs){
			recentMsgs.push(msg);
		}
		
		return recentMsgs;
	}
	
