	//获取当前时间
	index.getCurrentTime = function(flag) {
	    var currentTime = "";
	    var myDate = new Date();
	    var year = myDate.getFullYear();
	    var month = parseInt(myDate.getMonth().toString()) + 1; //month是从0开始计数的，因此要 + 1
	    if (month < 10) {
	        month = "0" + month.toString();
	    }
	    var date = myDate.getDate();
	    if (date < 10) {
	        date = "0" + date.toString();
	    }
	    var hour = myDate.getHours();
	    if (hour < 10) {
	        hour = "0" + hour.toString();
	    }
	    var minute = myDate.getMinutes();
	    if (minute < 10) {
	        minute = "0" + minute.toString();
	    }
	    var second = myDate.getSeconds();
	    if (second < 10) {
	        second = "0" + second.toString();
	    }
	    if(flag == "0")
	    {
	        currentTime = year.toString() + month.toString() + date.toString() + hour.toString() + minute.toString() + second.toString(); //返回时间的数字组合
	    }
	    else if(flag == "1")
	    {
	        currentTime = year.toString() + "-" + month.toString() + "-" + date.toString() + " " + hour.toString() + ":" + minute.toString() + ":" + second.toString(); //以时间格式返回
	    }
	    return currentTime;
	}
	
	//获取时间间隔：几小时几分钟 dt1<dt2
	index.getTimeInterval = function(dt1, dt2){
		//var dt1 = "2009-11-5 10:30";
		//var dt2 = "2009-11-6 9:20";
		var regTime = /(\d{4})-(\d{1,2})-(\d{1,2})( \d{1,2}:\d{1,2})/g;
		//alert(dt1.replace(regTime, "$2-$3-$1$4"));
		var interval = Math.abs(Date.parse(dt1.replace(regTime, "$2-$3-$1$4")) - Date.parse(dt2.replace(regTime, "$2-$3-$1$4")))/1000;
		var h = Math.floor(interval / 3600);
		var m = Math.floor(interval % 3600 / 60);
		var tl = '';
		if(h == 0){
			tl = m + "分钟";
		}else if(h > 0){
			tl = h + "小时 " + m + "分钟";
		}else{
			tl = "0分钟";;
		}
		return tl;
	}
    
    //截取字符串,中文为2个字符
    index.subString = function(str, len) 
    { 
        var newLength = 0; 
        var newStr = ""; 
        var chineseRegex = /[^\x00-\xff]/g; 
        var singleChar = ""; 
        str = new String(str);
        var strLength = str.replace(chineseRegex,"**").length; 
        for(var i = 0;i < strLength;i++) 
        { 
            singleChar = str.charAt(i).toString(); 
            if(singleChar.match(chineseRegex) != null) 
            { 
                newLength += 2; 
            }     
            else 
            { 
                newLength++; 
            } 
            if(newLength > len) 
            { 
                break; 
            } 
            newStr += singleChar; 
        } 
         
        if(strLength > len) 
        { 
            newStr += ".."; 
        } 
        return newStr; 
    }
    
